// Collision detection
function checkCollisions() {
  // Bullet vs enemies
  bullets.forEach((bullet, bulletIndex) => {
    enemies.forEach((enemy, enemyIndex) => {
      if (enemies.length && bullets.length) {
        const distance = bullet.mesh.position.distanceTo(enemy.position);
