            // Check bonus collision
            if (bonus && player.position.distanceTo(bonus.position) < 20) {
                scene.remove(bonus);
                bonus = null;
                const bonusType = Math.random();
                if (bonusType < 0.25) {
                    fireRate *= 1.1;
                } else if (bonusType < 0.5) {
                    bouncyFire = 5;
                } else if (bonusType < 0.75) {
                    guns++;
                } else {
                    lives++;
                }
            }

            // Update renderer
            renderer.render(scene, camera);
        }

        animate();
    } catch (error) {
        litechat.utils.log('error', 'âŒ FAILED to create scene:', error.message);
