}

// ---- Game Loop -----
function resetGame() {
  player.lives = PLAYER_INIT_LIVES;
  player.fireRate = 1;
  player.spread = 1;
  player.bounce = 0;
  player.x = 0; player.y = -GAME_H/2+80;
  player.invuln = 0;
  score = 0; 
  enemyDifficulty = 0;
  bullets = [];
  enemies = [];
  bonuses = [];
